/*
=====================================================
OMEGA19 Page
Checkout
=====================================================
*/

import React from 'react';
import Template from '../Template';
import Metadata from '../Metadata';

export default class Checkout extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            first_name: "",
            last_name: "",
            email: "",
            address1: "",
            address2: "",
            city: "",
            state_or_province: "",
            state_or_province_code: "",
            country_code: "",
            postal_code: "",
            phone: "",
            card_number: "",
            card_holder_name: "",
            expiry_month: "",
            expiry_year: "",
            verification_value: "",

            billing_address1: "",
            billing_address2: "",
            billing_city: "",
            billing_postal_code: "",
            billing_state: "",
            billing_state_code: "",
            billing_country_code: "",

            items: [],
            currency: "",
            taxIncluded: false,
            subtotal: 0,
            tax: 0,
            total: 0,

            isProcessing: false,

            isError: false,
            error: ""
        };

        global.API.getCart().then(
            (success) => {
                try {
                    let data = success.Result.Response.data;
                    this.setState({
                        currency: data.currency.code,
                        taxIncluded: data.tax_included,
                        items: data.line_items.physical_items,
                        subtotal: parseFloat(data.base_amount).toFixed(2),
                        total: parseFloat(data.cart_amount).toFixed(2),
                        tax: parseFloat(data.cart_amount - data.base_amount).toFixed(2),
                        isLoaded: true
                    });
                } catch(e) {

                }
            },
            (error) => {}
        );

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleCopyClick = this.handleCopyClick.bind(this);
    }
    handleChange(e) {
        const { name, type, value, id } = e.target;
        const val = type === "number" ? parseFloat(value) : value;
        this.setState({ [id]: val });
    }
    raiseProcessing() {
        this.setState({
            isProcessing: true
        });
    }
    stopProcessing() {
        this.setState({
            isProcessing: false
        });
    }
